class IconWindowPair extends React.Component {
    constructor(props) {
        super(props);
        let icon = null;
        if(this.props.type == "pdf"){
            icon = () => <Pdf onDoubleClick={this.toggleWindowOn} name={this.props.name}/>;
        }
        else{
            icon = () => <Folder onDoubleClick={this.toggleWindowOn} name={this.props.name}/>;
        }
        this.state = { windowToggle: false, pairName: props.name, fileType: this.props.type, icon: icon};
    }

    toggleWindowOn = (() => {
        this.setState((state) => {
            return { windowToggle: true, pairName: state.pairName, fileType: state.fileType, icon: state.icon};
        })
    });

    toggleWindowOff = (() => {
        this.setState((state) => {
            return { windowToggle: false, pairName: state.pairName, fileType: state.fileType, icon: state.icon};
        })
    });

    render() {
        return (
            <div id={this.state.id}>
                {this.state.icon()}
                <Window hideWindow={this.state.windowToggle} closeWindow={this.toggleWindowOff} pairName={this.state.pairName}/>
            </div>
        )
    };
}


