import React, { useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom/client';
import './style.css';
import { clickAndDrag } from './draggableDiv';

function Window(){
    const elmnt = useRef(null)
    useEffect(() =>{
        clickAndDrag(elmnt.current);
    });
    return(
        <div className="window" id="testWin" ref={elmnt}>
            <div className="windowHead" id="testWinHead">Moving window test</div>
            <p>Try</p>
            <p>it</p>
        </div>
    );
}

function Folder(){
    const elmnt = useRef(null)
    useEffect(() => {
        clickAndDrag(elmnt.current)
    });
    function cursorMove(){
        elmnt.classlist.add("moving")
    }
    return(
        <div >
            <img src={require('./resources/folder.png')} ref={elmnt} className="folder" onMouseDown={cursorMove()}/>
        </div> 
    );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Folder/>);
