import React, { useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom/client';
import './style.css';
import { clickAndDrag } from './draggableDiv';

function Window(props){
    console.log("double click registered")
    const elmnt = useRef(null);
    useEffect(() =>{
        clickAndDrag(elmnt.current);
    });
    return(
        <div className="window" id="testWin" ref={elmnt}>
            <div className="windowHead" id="testWinHead">
                Moving window test
            </div>
            <p>Try</p>
            <p>it</p>
        </div>
    );
}

function Folder(){
    const elmnt = useRef(null)
    useEffect(() => {
        clickAndDrag(elmnt.current)
    });

    function renderWindow(){
        console.log("double click")
        const root = ReactDOM.createRoot(
            document.getElementById('root')
          );
        return(
            <Window/>
        )
    }
    function toggleWindow(){
        root.render(Window("placeholder"))
    }
    return(
        <div ref={elmnt} className="folder" onDoubleClick={toggleWindow}>
            <img src={require('./resources/folder.png')}/>
            <h1>name</h1>
        </div> 
    );
}

class Desktop extends React.Component{
    constructor(props){
        super(props)
    }
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Folder/>);
