import React, { useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom/client';
import './style.css';
import { clickAndDrag } from './draggableDiv';

 function Window(props){
    const windowRef = React.createRef(null);
    const shown = props.windowToggle;
    useEffect(() =>{
        clickAndDrag(windowRef.current);
    });
    return(
        <div className="window" id="testWin" ref={windowRef}>
            <div className="windowHead" id="testWinHead">
                Moving window test
            </div>
            <p>Try</p>
            <p>it</p>
        </div>
    );
}

function Folder(props){
    const folderRef = React.createRef(null);
    useEffect(() => {
        clickAndDrag(folderRef.current)
    });

    return(
        <div ref={folderRef} className="folder" onDoubleClick={props.onDoubleClick}>
            <img src={require('./resources/folder.png')}/>
            <h1>name</h1>
        </div> 
    );
}

class Icon_Window_pair extends React.Component{
    constructor(props){
        super(props);
        this.state = {windowToggle: false, pairId: props.id};
    }

    toggleWindow = (() => {
        this.setState((state) => {
            return{windowToggle: !state.windowToggle, pairId: state.pairId};
        })
    });

    render(){
        return(
            <div id={this.state.id}>
                <Folder onDoubleClick={this.toggleWindow}/>
                <Window hideWindow={this.state.windowToggle}/>
            </div>
        )
    };
}

class Desktop extends React.Component{
    constructor(props){
        super(props)
    }

    render(){
        return(
            <div>
                <Icon_Window_pair id="folderTest"/>
            </div>
        )
    };
}
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Desktop/>);
