import React, { useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom/client';
import './style.css';
import { clickAndDrag } from './draggableDiv';

 function Window(props){
    const windowRef = React.createRef(null);
    const resizeRefT = React.createRef(null);
    const resizeRefL = React.createRef(null);
    const resizeRefR = React.createRef(null);
    const resizeRefB = React.createRef(null);

    const windowShown = props.hideWindow;
    const size  = props.windowSize;
    const closeFunc = (() =>{
        props.closeWindow();
    });
    const resizeFunc = ((e, direction, ref, d)=>{
        props.resizeFunc(e, direction, ref, d);
    });
    useEffect(() =>{
        console.log(`window ref is null: ${windowRef.current == null}`);
        if(windowRef.current != null){
            console.log("click+drag enabled for window");
            clickAndDrag(windowRef.current);

            const resizableEle = windowRef.current;
            const styles = window.getComputedStyle(resizableEle);

            let x = 0;
            let y = 0;
        };
    });
    if(windowShown){
        return(
            <div className="window" id="testWin" ref={windowRef}>
                <div ref={resizeRefT} className="resizer resizer-t"></div>
                <div ref={resizeRefL} className="resizer resizer-l"></div>
                <div ref={resizeRefR} className="resizer resizer-r"></div>
                <div ref={resizeRefB} className="resizer resizer-b"></div>
                <div className="windowHead" id="testWinHead">
                    <button className='closeButton' type='button' onClick={closeFunc}>x</button>
                    Moving window test
                </div>
                <p>Try</p>
                <p>it</p>
            </div>
        );
    }
    return null
}

function Folder(props){
    const folderRef = React.createRef(null);
    useEffect(() => {
        clickAndDrag(folderRef.current)
    });

    return(
        <div ref={folderRef} className="folder" onDoubleClick={props.onDoubleClick}>
            <img src={require('./resources/folder.png')}/>
            <h1>name</h1>
        </div> 
    );
}

class Icon_Window_pair extends React.Component{
    constructor(props){
        super(props);
        this.state = {windowToggle: false, pairId: props.id, windowsOpen: 0, windowSize:{height:200, width: 200}};
    }

    windowResize = ((e, direction, ref, d) => {
        this.setState((state) =>{
            return{windowToggle: state.windowToggle, pairId: state.pairId, windowsOpen: state.windowsOpen, windowSize:{width: state.width + d.width, height: state.height + d.height}};
        })
    })

    toggleWindowOn = (() => {
        this.setState((state) => {
            return{windowToggle: true, pairId: state.pairId, windowsOpen: state.windowsOpen + 1, windowSize: state.windowSize};
        })
    });

    toggleWindowOff =(() => {
        console.log("attempt to close window");
        this.setState((state) => {
            return{windowToggle: false, pairId: state.pairId, windowsOpen: state.windowsOpen - 1, windowSize: state.windowSize};
        })
    });

    render(){
        return(
            <div id={this.state.id}>
                <Folder onDoubleClick={this.toggleWindowOn}/>
                <Window hideWindow={this.state.windowToggle} closeWindow={this.toggleWindowOff} windowSize={this.state.windowSize} resizeFunc={this.windowResize}/>
            </div>
        )
    };
}

class Desktop extends React.Component{
    constructor(props){
        super(props)
    }

    render(){
        return(
            <div>
                <Icon_Window_pair id="folderTest"/>
            </div>
        )
    };
}
