import ReactDOM from 'react-dom/client';
import './style.css';
import { clickAndDrag } from './draggableDiv';

const window = (
    <div className="window" id="testWin" ref={props.ref}>
        <div className="windowHead" id="testWinHead">
            Moving window test
        </div>
        <p>Try</p>
        <p>it</p>
    </div>
);

useEffect(() => {
    clickAndDrag(props.ref.current)
});

const folder = (
    <div ref={props.ref} className="folder">{/*onDoubleClick={renderWindow}*/}
        <img src={require('./resources/folder.png')}/>
        <h1>name</h1>
    </div> 
);

function Icon_Window_pair(){
    const icon_ref = useRef(null);
    return(
        <div>
            <Folder ref={icon_ref}/>
        </div>
    );
}

class Desktop extends React.Component{
    constructor(props){
        super(props)
    }

    render(){
        return(
            <div>
                <p>Is this thing on?</p>
                <Icon_Window_pair/>
            </div>
        );
    };
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Icon_Window_pair/>);
