import React, { useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom/client';
import './style.css';
import { clickAndDrag } from './draggableDiv';

function Window(props){
    useEffect(() =>{
        clickAndDrag(this.props.ref.current);
    });
    return(
        <div className="window" id="testWin" ref={props.ref}>
            <div className="windowHead" id="testWinHead">
                Moving window test
            </div>
            <p>Try</p>
            <p>it</p>
        </div>
    );
}

function Folder(props){
    useEffect(() => {
        clickAndDrag(props.icon_ref.current)
    });

    return(
        <div ref={props.icon_ref} className="folder" >{/*$(this.getDOMNode())*/}
            <img src={require('./resources/folder.png')}/>
            <h1>name</h1>
        </div> 
    );
}

function Icon_Window_pair(){
    const icon_ref = useRef(null);
    return(
        <div>
            <Folder ref={icon_ref}/>
        </div>
    );
}

class Desktop extends React.Component{
    constructor(props){
        super(props)
    }

    render(){
        return(
            <div>
                <p>Is this thing on?</p>
                <Icon_Window_pair/>
            </div>
        );
    };
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Icon_Window_pair/>);
